import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Typography from '@material-ui/core/Typography';

const styles = theme => ({
    root: {
      ...theme.mixins.gutters(),
      paddingTop: theme.spacing.unit * 2,
      paddingBottom: theme.spacing.unit * 2,
    },
  });  

class Operations extends React.Component {
    state = {
        vendor: "",
        amount: "",
        category: ""
    }

    amountInputHandler = event => {
        this.setState({amount: event.target.value})
    }

    vendorInputHandler = event => {
        this.setState({vendor: event.target.value})
    }

    categoryInputHandler = event => {
        this.setState({category: event.target.value})
    }

    deposit = () => {
        const transcation = {
            id: null,
            ...this.state
        }
        this.props.deposit(transcation);
        this.props.history.push("/transactions");
    }

    withdraw = () => {
        const transcation = {
            id: null,
            ...this.state
        }
        this.props.withdraw(transcation);
        this.props.history.push("/transactions");
    }

    render() {

        return (
            <div>
                <input value={this.state.amount} onChange={this.amountInputHandler} placeholder="Amount"/>
                <input value={this.state.vendor} onChange={this.vendorInputHandler} placeholder="Vendor"/>
                <input value={this.state.category} onChange={this.categoryInputHandler} placeholder="Category"/>
                <button onClick={this.withdraw}>Withdraw</button>
                <button onClick={this.deposit}>Deposit</button>
                <div>
                <Paper elevation={1}>
                    <Typography variant="h5" component="h3">
                    This is a sheet of paper.
                    </Typography>
                    <Typography component="p">
                    Paper can be used to build surface or other elements for your application.
                    </Typography>
                </Paper>
                </div>
            </div>
        )
    }
}

export default Operations;
