import React from 'react';
import './App.css';
import Transcations from './components/Transcations';
import Operations from './components/Operations';

class App extends React.Component {
  state = {
    transcations: [
      { amount: 3200, vendor: "Elevation", category: "Salary" },
      { amount: -7, vendor: "Runescape", category: "Entertainment" },
      { amount: -20, vendor: "Subway", category: "Food" },
      { amount: -98, vendor: "La Baguetterie", category: "Food" }
    ]    
  }

  getBalance = () => {
    let sum = 0;
    this.state.transcations.forEach(transcation => sum+= transcation.amount);
    return sum;
  }

  removeTranscation = transcationIndex => {
    const allTranscations = [...this.state.transcations];
    allTranscations.splice(transcationIndex, 1);
    this.setState({transcations: allTranscations});
  }

  render() {
    return (
      <React.Fragment>
        <h1>Balance: {this.getBalance()}</h1>
        <Transcations removeTranscation={this.removeTranscation} transcations={this.state.transcations}/>
      </React.Fragment>
    )
  }
}


export default App;
