import React from 'react';
import { withStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Typography from '@material-ui/core/Typography';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';


const styles = theme => ({
    root: {
      ...theme.mixins.gutters(),
      paddingTop: theme.spacing.unit * 3,
      paddingBottom: theme.spacing.unit * 2,
    },
    button: {
        margin: theme.spacing.unit
    }
  });  

class Operations extends React.Component {
    state = {
        vendor: "",
        amount: "",
        category: ""
    }

    amountInputHandler = event => {
        this.setState({amount: event.target.value})
    }

    vendorInputHandler = event => {
        this.setState({vendor: event.target.value})
    }

    categoryInputHandler = event => {
        this.setState({category: event.target.value})
    }

    deposit = () => {
        const transcation = {
            id: null,
            ...this.state
        }
        this.props.deposit(transcation);
        this.props.history.push("/transactions");
    }

    withdraw = () => {
        const transcation = {
            id: null,
            ...this.state
        }
        this.props.withdraw(transcation);
        this.props.history.push("/transactions");
    }

    render() {
        const classes = this.props
        return (
            <div>
                <input value={this.state.amount} onChange={this.amountInputHandler} placeholder="Amount"/>
                <input value={this.state.vendor} onChange={this.vendorInputHandler} placeholder="Vendor"/>
                <input value={this.state.category} onChange={this.categoryInputHandler} placeholder="Category"/>
                <button onClick={this.withdraw}>Withdraw</button>
                <button onClick={this.deposit}>Deposit</button>
                <div>
                <Paper elevation={3}>
                    <Typography variant="h4" component="h1">
                    Withdraw / Deposit
                    </Typography>
                    <TextField
                    id="outlined-dense"
                    label="Amount"
                    margin="dense"
                    variant="outlined"
                    />
                    <br />
                    <TextField
                    id="outlined-dense"
                    label="Vendor"
                    margin="dense"
                    variant="outlined"
                    />
                    <br />
                    <TextField
                    id="outlined-dense"
                    label="Category"
                    margin="dense"
                    variant="outlined"
                    />
                    <br />
                    <Button variant="contained" color="primary">Withdraw</Button>
                    <Button variant="contained" color="primary">Deposit</Button>
                </Paper>
                </div>
            </div>
        )
    }
}

export default withStyles(styles)(Operations);
