import React from 'react';
import TableRow from '@material-ui/core/TableRow';
import { withStyles } from '@material-ui/core/styles';

const CustomTableCell = withStyles(theme => ({
    head: {
      backgroundColor: theme.palette.common.black,
      color: theme.palette.common.white,
    },
    body: {
      fontSize: 14,
    },
}))(TableCell);

const styles = theme => ({
    root: {
      width: '100%',
      marginTop: theme.spacing.unit * 3,
      overflowX: 'auto',
    },
    table: {
      minWidth: 700,
    },
    row: {
      '&:nth-of-type(odd)': {
        backgroundColor: theme.palette.background.default,
      },
    },
});


const transcation = props => {
    const { classes } = props

    const removeTranscation = () => {
        props.removeTranscationById(props.transcation._id)
    }

    return (
        <TableRow className={classes.row}>
            <CustomTableCell component="th" scope="row">
            {props.transcation.amount}
            </CustomTableCell>
            <CustomTableCell align="right">{props.transcation.vendor}</CustomTableCell>
            <CustomTableCell align="right">{props.transcation.category}</CustomTableCell>
            <CustomTableCell align="right">{props.transcation.date || null}</CustomTableCell>
            <CustomTableCell align="right">
                <Button onClick={removeTranscation} variant="contained" color="primary">Remove</Button>
            </CustomTableCell>
        </TableRow>
    )
}

export default withStyles(styles)(transcation);


