import React from 'react';
import './App.css';
import Transcations from './components/Transcations';
import Operations from './components/Operations';

class App extends React.Component {
  state = {
    transcations: [
      { id: 1, amount: 3200, vendor: "Elevation", category: "Salary" },
      { id: 2, amount: -7, vendor: "Runescape", category: "Entertainment" },
      { id: 3, amount: -20, vendor: "Subway", category: "Food" },
      { id: 4, amount: -98, vendor: "La Baguetterie", category: "Food" }
    ]    
  }

  getBalance = () => {
    let sum = 0;
    this.state.transcations.forEach(transcation => sum+= transcation.amount);
    return sum;
  }

  removeTranscationById = transcationId => {
    const allTranscations = [...this.state.transcations];
    this.setState({transcations: allTranscations.filter(transcation => transcation.id !== transcationId)});
  }

  withdraw = newTranscation => {
    newTranscation.id = this.state.transcations.length + 1;
    this.setState({transcations: [...this.state.transcations, newTranscation]});
  }

  deposit = newTranscation => {
    newTranscation.id = this.state.transcations.length + 1;
    newTranscation.amount = newTranscation.amount * -1;
    this.setState({transcations: [...this.state.transcations, newTranscation]});
  }

  render() {
    return (
      <React.Fragment>
        <h1>Balance: {this.getBalance()}</h1>
        <Transcations removeTranscationById={this.removeTranscationById} transcations={this.state.transcations}/>
        <Operations withdraw={this.withdraw} deposit={this.deposit}/>
      </React.Fragment>
    )
  }
}


export default App;
